mapper.select:
select placeNum, placeName, placeCategory, placeRoadAddr, placeLat, placeLong, placeImage, placelike, placeScore, destinationNum from place where destinationNum = #{destinationNum} and placeCategory in
#{category}
order by placelike desc

mapper.insert:
insert into place(
    placeName, placeCategory, placeRoadAddr, placeDetailAddr,
    placeLat, placeLong, placeImage,
    placeContent, destinationNum, placelike, placeScore
)
values (
    #{placeName}, #{placeCategory}, #{placeRoadAddr}, #{placeDetailAddr},
    #{placeLat}, #{placeLong}, #{placeImage},
    #{placeContent}, #{destinationNum}, #{placelike}, #{placeScore}
)

mapper.select:
select p.* from plan pl
join place p on pl.placeNum = p.placeNum
where pl.scheduleNum = #{scheduleNum}

SELECT * FROM place
ORDER BY placeNum DESC
LIMIT #{start}, #{pageSize}
